mapper.delete:
delete from jenkins_trigger_config

  ID = #{id,jdbcType=INTEGER}

    and DEPART = #{depart,jdbcType=VARCHAR}

mapper.select:
select ID id,SOURCE source from jenkins_trigger_config
SOURCE !=''
and DEPART = #{depart,jdbcType=VARCHAR}

select ID id,JENKINS_FLAVOR jenkinsFlavor from jenkins_trigger_config
JENKINS_FLAVOR !=''
and DEPART = #{depart,jdbcType=VARCHAR}

select ID id,JENKINS_VERSION jenkinsVersion from jenkins_trigger_config
JENKINS_VERSION !=''
and DEPART = #{depart,jdbcType=VARCHAR}
