mapper.insert:

  SELECT LAST_INSERT_ID()

insert into push_group (TOPIC_NAME, TOPIC_CODE, CREATE_USER, 
  CREATE_TIME, Q_R_CODE, Q_R_CODE_UPDATE_TIME
  )
values (#{topicName,jdbcType=VARCHAR}, #{topicCode,jdbcType=VARCHAR}, #{createUser,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{qRCode,jdbcType=VARCHAR}, #{qRCodeUpdateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from push_group


    and TOPIC_NAME like CONCAT('%',CONCAT(#{topicName},'%'))


    and TOPIC_CODE = #{topicCode,jdbcType=VARCHAR}

    and  CREATE_USER = #{createUser,jdbcType=INTEGER}

order by  CREATE_TIME desc
